    private void getLastChar(){
        String texto = jTextField1.getText().toLowerCase();
        if(texto.length()>0)
        for(int i=texto.length()-1; i>0; i--){
            char aux = texto.charAt(i);
            if(aux == 'a' || aux == 'e' || aux == 'i' || aux == 'o' || aux == 'u'){
                jLabel2.setText(""+aux);
                break;
            }
        }
        else{
            jLabel2.setText("Ingresa algo");
        }
    }
